","keccak256":"0x8891738ffe910f0cf2da09566928589bf5d63f4524dd734fd9cedbac3274dd5c","license":"MIT"},"contracts/core/SmartWallet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {SmartWalletStorage} from "./abstract/SmartWalletStorage.sol";
import {ISmartWalletFactory} from "../interface/ISmartWalletFactory.sol";
import {IWormhole} from "../interface/IWormhole.sol";
import {ITokenBridge} from "../interface/ITokenBridge.sol";
import {IWETH} from "../interface/IWeth.sol";
import {IERC20, SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";


contract SmartWallet is SmartWalletStorage{

    using SafeERC20 for IERC20;

    function initialize(
        address owner_, 
        address agent_,
        address factory_,
        bytes32 user_,
        address wormhole_,
        address tokenBridge_,
        address weth_,
        uint16 chainId_
    ) external initializer {
        require(owner_ != address(0), "owner is zero!");
        require(agent_ != address(0), "agent is zero");
        __ReentrancyGuard_init();
        _transferOwnership(owner_);
        agent = agent_;
        user = user_;
        factory = ISmartWalletFactory(factory_);
        wormhole = IWormhole(wormhole_);
        tokenBridge = ITokenBridge(tokenBridge_);
        weth = IWETH(weth_);
        chainId = chainId_;
    }

    function transferTokenBackToSmartWallet(
        address token,
        uint256 amount,
        uint256 arbiterFee,
        uint32 nonce
    ) external onlyAuthorized {
        uint256 wormholeFee = wormhole.messageFee();
        IERC20(token).approve(address(tokenBridge), amount);
        tokenBridge.transferTokens{value: wormholeFee}(
            token,
            amount, 
            chainId,
            user,
            arbiterFee,
            nonce
        );
    }
}","keccak256":"0xff77ddeaae8421bad67b58a964619d54e1e9dc442298530ee8cc1e1df93cc99c","license":"MIT"},"contracts/core/abstract/SmartWalletStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {ISmartWalletFactory} from "../../interface/ISmartWalletFactory.sol";
import {IWormhole} from "../../interface/IWormhole.sol";
import {ITokenBridge} from "../../interface/ITokenBridge.sol";
import {IWETH} from "../../interface/IWeth.sol";

abstract contract SmartWalletStorage is OwnableUpgradeable, ReentrancyGuardUpgradeable {
    bytes32 public user;
    address public agent;
    address public pendingAdmin;
    ISmartWalletFactory public factory;
    IWormhole public wormhole;
    ITokenBridge public tokenBridge;
    IWETH public weth;
    uint16 chainId;

    modifier onlyAuthorized() {
        require(msg.sender == owner() || msg.sender == agent, "Not Authorized!");
        _;
    }

    modifier onlyPendingAdmin {
        require(msg.sender == pendingAdmin, "Not Authorized!");
        _;
    }

    function changeAgent(address new_agent) external onlyOwner {
        agent = new_agent;
    }

    function proposeNewAdmin(address new_admin) external onlyOwner {
        pendingAdmin = new_admin;
    }

    function acceptAdminRole() external onlyPendingAdmin {
        _transferOwnership(pendingAdmin);
        pendingAdmin = address(0);
    } 
}","keccak256":"0x35795825d4c366c3542fb85f3bac6622d58168d7c1759e7f6c2d98245a1e5dcf","license":"MIT"},"contracts/interface/ISmartWalletFactory.sol":{"content":"// SPDX-License-Identifier: MIT
