contracts/core/abstract/SmartWalletFacto.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {ISmartWalletBeacon} from "../../interface/ISmartWalletBeacon.sol";
import {ISmartWalletFactory} from "../../interface/ISmartWalletFactory.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

abstract contract SmartWalletFactoryStorage is ISmartWalletFactory, OwnableUpgradeable{
    using EnumerableSet for EnumerableSet.AddressSet;
    ISmartWalletBeacon public SmartWalletBeacon;
    EnumerableSet.AddressSet internal _wallets;
    mapping(bytes32 => address) public walletsByUser;
    address public router;
    address public agent;

    modifier onlyAuthorization() {
        require(msg.sender == owner() || msg.sender == agent || tx.origin == agent, "not authorized!");
        _;
    }
}

contracts/core/abstract/SmartWalletStora.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {ISmartWalletFactory} from "../../interface/ISmartWalletFactory.sol";
import {IWormhole} from "../../interface/IWormhole.sol";
import {ITokenBridge} from "../../interface/ITokenBridge.sol";
import {IWETH} from "../../interface/IWeth.sol";

abstract contract SmartWalletStorage is OwnableUpgradeable, ReentrancyGuardUpgradeable {
    bytes32 public user;
    address public agent;
    address public pendingAdmin;
    ISmartWalletFactory public factory;
    IWormhole public wormhole;
    ITokenBridge public tokenBridge;
    IWETH public weth;
    uint16 chainId;

    modifier onlyAuthorized() {
        require(msg.sender == owner() || msg.sender == agent, "Not Authorized!");
        _;
    }

    modifier onlyPendingAdmin {
        require(msg.sender == pendingAdmin, "Not Authorized!");
        _;
    }

    function changeAgent(address new_agent) external onlyOwner {
        agent = new_agent;
    }

    function proposeNewAdmin(address new_admin) external onlyOwner {
        pendingAdmin = new_admin;
    }

    function acceptAdminRole() external onlyPendingAdmin {
        _transferOwnership(pendingAdmin);
        pendingAdmin = address(0);
    } 
}

contracts/core/SmartWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {SmartWalletStorage} from "./abstract/SmartWalletStorage.sol";
import {ISmartWalletFactory} from "../interface/ISmartWalletFactory.sol";
import {IWormhole} from "../interface/IWormhole.sol";
import {ITokenBridge} from "../interface/ITokenBridge.sol";
import {IWETH} from "../interface/IWeth.sol";

contract SmartWallet is SmartWalletStorage{

    function initialize(
        address owner_, 
        address agent_,
        address factory_,
        bytes32 user_,
        address wormhole_,
        address tokenBridge_,
        address weth_,
        uint16 chainId_
    ) external initializer {
        require(owner_ != address(0), "owner is zero!");
        require(agent_ != address(0), "agent is zero");
        __ReentrancyGuard_init();
        _transferOwnership(owner_);
        agent = agent_;
        user = user_;
        factory = ISmartWalletFactory(factory_);
        wormhole = IWormhole(wormhole_);
        tokenBridge = ITokenBridge(tokenBridge_);
        weth = IWETH(weth_);
        chainId = chainId_;
    }

    function transferTokenBackToSmartWallet(
        address token,
        uint256 amount,
        uint256 arbiterFee,
        uint32 nonce
    ) external onlyAuthorized {
        uint256 wormholeFee = wormhole.messageFee();
        tokenBridge.transferTokens{value: wormholeFee}(
            token,
            amount, 
            chainId,
            user,
            arbiterFee,
            nonce
        );
    }
}

contracts/core/SmartWalletFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {ISmartWallet} from "../interface/ISmartWallet.sol";
import {ISmartWalletBeacon} from "../interface/ISmartWalletBeacon.sol";
import {SmartWalletFactoryStorage} from "./abstract/SmartWalletFactoryStorage.sol";
import {
    BeaconProxy
} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";


contract SmartWalletFactory is SmartWalletFactoryStorage {
    using EnumerableSet for EnumerableSet.AddressSet;

    function initialize(address new_owner, address router_, address agent_, address beacon) external initializer {
        require(new_owner != address(0), "Owner is address zero!");
        require(beacon != address(0), "Beacon is address zero!");
        SmartWalletBeacon = ISmartWalletBeacon(beacon);
        __Ownable_init(new_owner);
        _transferOwnership(new_owner);
        router = router_;
        agent = agent_;
    }

    function createSmartWallet(
        address agent,
        bytes32 user,
        address wormhole,
        address tokenBridge,
        address weth,
        uint16 chainId
    ) external onlyAuthorization returns(address) {
        require(walletsByUser[user] == address(0), "Already initialized");
        address wallet = _preDeploy(agent, user, wormhole, tokenBridge, weth, chainId);
        walletsByUser[user] = wallet;
        _wallets.add(wallet);
        return wallet;
    }

    function _preDeploy(
        address agent,
        bytes32 user,
        address wormhole,
        address tokenBridge,
        address weth,
        uint16 chainId
    ) internal returns (address newWallet) {
        bytes memory data = abi.encodeWithSelector(
            ISmartWallet.initialize.selector,
            owner(),
            agent,
            address(this),
            user,
            wormhole,
            tokenBridge,
            weth,
            chainId
        );

        bytes32 salt = keccak256(
            abi.encodePacked(tx.origin, block.number, data)
        );

        newWallet = address(
            new BeaconProxy{salt: salt}(address(SmartWalletBeacon), data)
        );
    }
}

