pragma solidity ^0.8.28;

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {SmartWalletRouterStorage} from "./SmartWalletRouterStorage.sol";
import {ISmartWalletFactory} from "./interface/ISmartWalletFactory.sol";

contract SmartWalletRouter is SmartWalletRouterStorage {

    function initialize(
        address new_owner,
        address factory_,
        address agent_
    ) external initializer {
        require(agent_ != address(0), "");
        require(factory_ != address(0), "");
        factory = ISmartWalletFactory(factory_);
        agent = agent_;
        __Ownable_init(new_owner);
        __UUPSUpgradeable_init();
        __Pausable_init();
        __ReentrancyGuard_init();
        _transferOwnership(new_owner);
    }

    // function createSmartWallet(bytes32 user) external onlyAgentOrOwner {
    //     address wallet = factory.createSmartWallet(agent, user);
    //     walletByUser[user] = wallet;
    // }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    receive() external payable {}
}","keccak256":"0xa139cc4dca83020c1982a1ba81e7f042c54fb96f3e6df2815168ddd203a14880","license":"MIT"},"contracts/SmartWalletRouterStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {
    PausableUpgradeable
} from "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {
    ReentrancyGuardUpgradeable
} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {
    UUPSUpgradeable
} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {ISmartWalletFactory} from "./interface/ISmartWalletFactory.sol";

abstract contract SmartWalletRouterStorage is
    UUPSUpgradeable,
    OwnableUpgradeable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable 
{
    ISmartWalletFactory public factory;
    address agent;
    mapping(bytes32 => address) public walletByUser;

    modifier onlyAgent() {
        require(msg.sender == agent, "Not authorized");
        _;
    }

    modifier onlyAgentOrOwner() {
        require(msg.sender == agent || msg.sender == owner(), "Not authorized");
        _;
    }

    function changeAgent(address new_agent) external onlyOwner {
        agent = new_agent;
    }
}","keccak256":"0x58fe44b0edea1dae42b83d5ede02cddf32f08707207c40b81cf029151fe88b34","license":"MIT"},"contracts/interface/ISmartWalletFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

interface ISmartWalletFactory {
   function initialize(address new_owner, address router, address agent, address beacon) external;
   function createSmartWallet(
        address agent,
        bytes32 user,
        address wormhole,
        address tokenBridge,
        address weth,
        uint16 chainId
    ) external returns(address);
}","keccak256":"0x5e4701f6e145b4debbb79e9431b36262b9f4e155fd7e924d24477e7a8e346f2f","license":"MIT"}},"version":1}

