        address weth,
        uint16 chainId
    ) external returns(address);
}","keccak256":"0x5e4701f6e145b4debbb79e9431b36262b9f4e155fd7e924d24477e7a8e346f2f","license":"MIT"},"contracts/interface/ITokenBridge.sol":{"content":"
