pragma solidity ^0.8.28;

import {ISmartWallet} from "../interface/ISmartWallet.sol";
import {ISmartWalletBeacon} from "../interface/ISmartWalletBeacon.sol";
import {SmartWalletFactoryStorage} from "./abstract/SmartWalletFactoryStorage.sol";
import {
    BeaconProxy
} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";


contract SmartWalletFactory is SmartWalletFactoryStorage {
    using EnumerableSet for EnumerableSet.AddressSet;

    function initialize(address new_owner, address router_, address agent_, address beacon) external initializer {
        require(new_owner != address(0), "Owner is address zero!");
        require(beacon != address(0), "Beacon is address zero!");
        SmartWalletBeacon = ISmartWalletBeacon(beacon);
        __Ownable_init(new_owner);
        _transferOwnership(new_owner);
        router = router_;
        agent = agent_;
    }

    function createSmartWallet(
        address agent,
        bytes32 user,
        address wormhole,
        address tokenBridge,
        address weth,
        uint16 chainId
    ) external onlyAuthorization returns(address) {
        require(walletsByUser[user] == address(0), "Already initialized");
        address wallet = _preDeploy(agent, user, wormhole, tokenBridge, weth, chainId);
        walletsByUser[user] = wallet;
        _wallets.add(wallet);
        return wallet;
    }

    function _preDeploy(
        address agent,
        bytes32 user,
        address wormhole,
        address tokenBridge,
        address weth,
        uint16 chainId
    ) internal returns (address newWallet) {
        bytes memory data = abi.encodeWithSelector(
            ISmartWallet.initialize.selector,
            owner(),
            agent,
            address(this),
            user,
            wormhole,
            tokenBridge,
            weth,
            chainId
        );

        bytes32 salt = keccak256(
            abi.encodePacked(tx.origin, block.number, data)
        );

        newWallet = address(
            new BeaconProxy{salt: salt}(address(SmartWalletBeacon), data)
        );
    }
}","keccak256":"0xb765d059a8458347193cd5a19cc4a167af6dd729a866039f2463277b331f100d","license":"MIT"},"contracts/core/abstract/SmartWalletFactoryStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {ISmartWalletBeacon} from "../../interface/ISmartWalletBeacon.sol";
import {ISmartWalletFactory} from "../../interface/ISmartWalletFactory.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

abstract contract SmartWalletFactoryStorage is ISmartWalletFactory, OwnableUpgradeable{
    using EnumerableSet for EnumerableSet.AddressSet;
    ISmartWalletBeacon public SmartWalletBeacon;
    EnumerableSet.AddressSet internal _wallets;
    mapping(bytes32 => address) public walletsByUser;
    address public router;
    address public agent;

    modifier onlyAuthorization() {
        require(msg.sender == owner() || msg.sender == agent || tx.origin == agent, "not authorized!");
        _;
    }
}","keccak256":"0xdeb9e7938544908dec0e2c56f67248acfcc7b435287eb71d30f27aad564953eb","license":"MIT"},"contracts/interface/ISmartWallet.sol":{"content":"// SPDX-License-Identifier: MIT
